// ====== CONFIGURACI√ìN GENERAL ======

// Frases motivacionales aleatorias üí¨
const frases = [
  "üî• Lo est√°s haciendo excelente, no bajes el ritmo.",
  "‚ú® Constancia y foco, que los resultados ya se notan.",
  "üöÄ Semana nueva, oportunidades nuevas.",
  "üí™ Gran progreso, sigamos construyendo resultados.",
  "üåü Orgullosa del esfuerzo que est√°n poniendo, sigamos as√≠."
];

// ====== FECHAS ======
const hoy = new Date();
const inicioSemana = new Date(hoy);
inicioSemana.setDate(hoy.getDate() - 7); // lunes anterior
const finSemana = new Date(hoy);
finSemana.setDate(hoy.getDate() - 1); // domingo anterior

function formatDate(date) {
  return date.toLocaleDateString('es-AR', { day: '2-digit', month: 'long' });
}

// ====== COLORES Y ESTILO ======
const colorPrincipal = "#2C5EFF";
const colorSecundario = "#F5F7FF";
const colorTexto = "#333";
const colorLinea = "#e0e0e0";

// üí° Inserta aqu√≠ el link del logo de GarantiaYA:
const logoURL = "https://drive.google.com/uc?export=view&id=1C7pT48gOgb3b3KhtxxEOQDqwTRnECQ3K";

// ====== DATOS ======
const rows = items.map(i => i.json);
const correos = [];

// ====== INICIO DEL RESUMEN GENERAL ======
let resumenEquipo = `
<body style="font-family: 'Segoe UI', Arial, sans-serif; background:${colorSecundario}; padding:20px; color:${colorTexto};">
  <table style="max-width:600px; margin:auto; background:white; border-radius:12px; box-shadow:0 2px 6px rgba(0,0,0,0.08); padding:24px;">
    <tr>
      <td style="text-align:center;">
        <img src="${logoURL}" alt="GarantiaYA" style="height:50px; margin-bottom:10px;">
        <h2 style="color:${colorPrincipal}; margin-bottom:4px;">üíº Resumen general ‚Äì Equipo CNT</h2>
        <p style="margin:0; font-size:14px; color:#666;">Semana ${formatDate(inicioSemana)} al ${formatDate(finSemana)}</p>
      </td>
    </tr>
    <tr><td><hr style="border:0; border-top:1px solid ${colorLinea}; margin:16px 0;"></td></tr>`;

// ====== C√ÅLCULO DE TOTALES ======
let totalEsperado = { Leads: 0, PA: 0, Ventas: 0 };
let totalRealizado = { Leads: 0, PA: 0, Ventas: 0 };

for (const d of rows) {
  totalEsperado.Leads += Number(d["Leads Esperado"] || 0);
  totalEsperado.PA += Number(d["PA Esperado"] || 0);
  totalEsperado.Ventas += Number(d["Ventas Esperado"] || 0);

  totalRealizado.Leads += Number(d["Leads Realizados"] || 0);
  totalRealizado.PA += Number(d["PA Realizados"] || 0);
  totalRealizado.Ventas += Number(d["Ventas Realizadas"] || 0);
}

const totalLeadsPerc = ((totalRealizado.Leads / totalEsperado.Leads) * 100).toFixed(0);
const totalPAPerc = ((totalRealizado.PA / totalEsperado.PA) * 100).toFixed(0);
const totalVentasPerc = ((totalRealizado.Ventas / totalEsperado.Ventas) * 100).toFixed(0);

// ====== CORREOS INDIVIDUALES ======
for (const d of rows) {
  const asesora = d.Asesora;
  const leadsPerc = ((d["Leads Realizados"] / d["Leads Esperado"]) * 100).toFixed(0);
  const paPerc = ((d["PA Realizados"] / d["PA Esperado"]) * 100).toFixed(0);
  const ventasPerc = ((d["Ventas Realizadas"] / d["Ventas Esperado"]) * 100).toFixed(0);
  const frase = frases[Math.floor(Math.random() * frases.length)];

  const html = `
  <body style="font-family: 'Segoe UI', Arial, sans-serif; background:${colorSecundario}; padding:20px; color:${colorTexto};">
    <table style="max-width:600px; margin:auto; background:white; border-radius:12px; box-shadow:0 2px 6px rgba(0,0,0,0.08); padding:24px;">
      <tr>
        <td style="text-align:center;">
          <img src="${logoURL}" alt="GarantiaYA" style="height:50px; margin-bottom:10px;">
          <h2 style="color:${colorPrincipal}; margin-bottom:4px;">üìà Reporte semanal ‚Äì Equipo CNT</h2>
          <p style="margin:0; font-size:14px; color:#666;">Semana ${formatDate(inicioSemana)} al ${formatDate(finSemana)}</p>
        </td>
      </tr>
      <tr><td><hr style="border:0; border-top:1px solid ${colorLinea}; margin:16px 0;"></td></tr>
      <tr><td>
        <p style="font-size:16px;">üåû Hola <b>${asesora}</b>!</p>
        <p style="margin-bottom:8px;">üëâ <b>Resumen de la semana pasada:</b><br>
        Esperado: ${d["Leads Esperado"]} Leads | ${d["PA Esperado"]} PA | ${d["Ventas Esperado"]} Ventas<br>
        Realizado: ${d["Leads Realizados"]} Leads | ${d["PA Realizados"]} PA | ${d["Ventas Realizadas"]} Ventas</p>

        <div style="background:${colorSecundario}; border-radius:10px; padding:10px; margin:10px 0;">
          <p style="margin:0 0 4px 0;"><b>‚úÖ Cumplimiento:</b></p>
          <p style="margin:2px 0;">üì• Leads: <b>${leadsPerc}%</b></p>
          <p style="margin:2px 0;">üì§ PA: <b>${paPerc}%</b></p>
          <p style="margin:2px 0;">üí∞ Ventas: <b>${ventasPerc}%</b></p>
        </div>

        <p style="margin:10px 0;">${d.Comentario || ""}</p>
        <p style="font-style:italic; color:${colorPrincipal};">${frase}</p>
        <p>üí™ Vamos con todo esta semana üöÄ</p>
      </td></tr>
      <tr><td><hr style="border:0; border-top:1px solid ${colorLinea}; margin:16px 0;"></td></tr>
      <tr><td style="text-align:center; font-size:12px; color:#999;">
        Reporte automatizado de <b>GarantiaYA ‚Äì Canal No Tradicional</b><br>
        ${new Date().toLocaleDateString('es-AR')}
      </td></tr>
    </table>
  </body>`;

  correos.push({ json: { tipo: "individual", asesora, html } });

  // A√±adir al desglose general
  resumenEquipo += `
    <tr><td>
      <p><b>üßç‚Äç‚ôÄÔ∏è ${asesora}</b><br>
      üì• Leads: ${leadsPerc}% (${d["Leads Realizados"]}/${d["Leads Esperado"]})<br>
      üì§ PA: ${paPerc}% (${d["PA Realizados"]}/${d["PA Esperado"]})<br>
      üí∞ Ventas: ${ventasPerc}% (${d["Ventas Realizadas"]}/${d["Ventas Esperado"]})</p>
      <hr style="border:0; border-top:1px dashed ${colorLinea}; margin:10px 0;">
    </td></tr>`;
}

// ====== CIERRE DEL RESUMEN GENERAL ======
resumenEquipo += `
    <tr><td>
      <h3 style="color:${colorPrincipal};">üìä Totales del equipo</h3>
      <p>üë• Leads: <b>${totalRealizado.Leads}</b> / ${totalEsperado.Leads} (${totalLeadsPerc}%)<br>
         üì§ PA: <b>${totalRealizado.PA}</b> / ${totalEsperado.PA} (${totalPAPerc}%)<br>
         üí∞ Ventas: <b>${totalRealizado.Ventas}</b> / ${totalEsperado.Ventas} (${totalVentasPerc}%)</p>
      <hr style="border:0; border-top:1px solid ${colorLinea}; margin:16px 0;">
      <p style="text-align:center; font-size:12px; color:#999;">
        Reporte automatizado de <b>GarantiaYA ‚Äì Canal No Tradicional</b><br>
        ${new Date().toLocaleDateString('es-AR')}
      </p>
    </td></tr>
  </table>
</body>`;

// Agregar correo general (para ti)
correos.push({ json: { tipo: "general", html: resumenEquipo } });

// ====== DEVOLVER ======
return correos;
